function (player, spacetracker) 
{
    dice_rolls &lt;- dice$roll()
    double &lt;- dice_rolls[1] == dice_rolls[2]
    player$double_straight &lt;- 0
    player$was_in_jail &lt;- player$in_jail
    if (player$was_in_jail) {
        if (double | player$rounds_in_jail == 2) {
            player$in_jail &lt;- FALSE
            if (player$verbose & double) {
                cat("In jail but rolled doubles.\n")
            }
            else if (player$verbose & player$rounds_in_jail == 
                2) {
                cat("Player's third turn in jail. Player must exit jail.\n")
            }
            player$rounds_in_jail &lt;- 0
            if (player$verbose) {
                cat("Player exits jail.\n")
            }
            player$move_fwd(sum(dice_rolls))
            spacetracker$tally(player$pos)
        }
        else {
            player$jail_turn()
            if (player$verbose) {
                cat("Player stays in jail.\n")
            }
            spacetracker$tally(player$pos)
        }
    }
    while (double & !player$was_in_jail) {
        player$double_turn()
        if (player$verbose) {
            cat("Doubles count is now ", player$double_straight, 
                ".\n", sep = "")
        }
        if (player$double_straight &lt; 3) {
            player$move_fwd(sum(dice_rolls))
            if (player$pos %in% c(3, 18, 34)) {
                spacetracker$tally(player$pos)
                if (player$verbose) {
                  cat("Draw a Community Chest card.\n")
                }
                card_index &lt;- community$draw()
                card &lt;- communitydeck[card_index, "card"]
                if (card == "Advance to Go") {
                  player$pos &lt;- 1
                  if (player$verbose) {
                    cat("Player moves to", player$pos, ": ", 
                      gameboard$title[player$pos], ".\n", sep = "")
                  }
                  spacetracker$tally(player$pos)
                }
                else if (card == " Go to Jail") {
                  player$pos &lt;- 11
                  player$in_jail &lt;- TRUE
                  if (player$verbose) {
                    cat("Player goes to jail.\n")
                  }
                  spacetracker$tally(player$pos)
                  break
                }
            }
            else if (player$pos %in% c(8, 23, 37)) {
                spacetracker$tally(player$pos)
                if (player$verbose) {
                  cat("Draw a Chance card.\n")
                }
                card_index &lt;- chance$draw()
                card &lt;- chancedeck[card_index, "card"]
                player$chance_movement(card)
                if (card %in% chancedeck$card[1:9]) {
                  spacetracker$tally(player$pos)
                }
                if (card %in% chancedeck$card[8]) {
                  break
                }
            }
            else if (player$pos == 31) {
                player$pos &lt;- 11
                player$in_jail &lt;- TRUE
                if (player$verbose) {
                  cat("Player goes to jail.\n")
                }
                spacetracker$tally(player$pos)
                break
            }
            else {
                spacetracker$tally(player$pos)
            }
            if (player$verbose) {
                cat("\nPlayer rolled doubles, so they take another turn.\n")
            }
            dice_rolls &lt;- dice$roll()
            double &lt;- dice_rolls[1] == dice_rolls[2]
        }
        else {
            if (player$verbose) {
                cat("Player goes to jail.\n")
            }
            player$double_straight &lt;- 0
            player$pos &lt;- 11
            player$in_jail &lt;- TRUE
            spacetracker$tally(player$pos)
            break
        }
    }
    if (!double & !player$was_in_jail) {
        player$move_fwd(sum(dice_rolls))
    }
    if (player$pos == 31) {
        player$pos &lt;- 11
        player$in_jail &lt;- TRUE
        if (player$verbose) {
            cat("Player goes to jail.\n")
        }
        spacetracker$tally(player$pos)
    }
    else if (player$pos %in% c(3, 18, 34)) {
        spacetracker$tally(player$pos)
        if (player$verbose) {
            cat("Draw a Community Chest card.\n")
        }
        card_index &lt;- community$draw()
        card &lt;- communitydeck[card_index, "card"]
        if (card == communitydeck$card[1]) {
            player$pos &lt;- 1
            if (player$verbose) {
                cat("Player moves to", player$pos, ": ", gameboard$title[player$pos], 
                  ".\n", sep = "")
            }
            spacetracker$tally(player$pos)
        }
        else if (card == communitydeck$card[2]) {
            player$pos &lt;- 11
            player$in_jail &lt;- TRUE
            if (player$verbose) {
                cat("Player goes to jail.\n")
            }
            spacetracker$tally(player$pos)
        }
    }
    else if (player$pos %in% c(8, 23, 37)) {
        spacetracker$tally(player$pos)
        if (player$verbose) {
            cat("Draw a Chance card.\n")
        }
        card_index &lt;- chance$draw()
        card &lt;- chancedeck[card_index, "card"]
        player$chance_movement(card)
        if (card %in% chancedeck$card[1:9]) {
            spacetracker$tally(player$pos)
        }
    }
    else if (player$was_in_jail == FALSE & player$in_jail == 
        FALSE) {
        spacetracker$tally(player$pos)
    }
}
